import React, { useEffect, useState } from "react";
import NavBar from "../components/NavBar";
import TopBar from "../components/TopBar";
import { Link } from "react-router-dom";
import axios from "axios";
// import UserDetails from "../userDetails/UserDetails.jsx";

const URL = "http://localhost:5000/user";

const fetchHandler = async () => {
  return await axios.get(URL).then((res) => res.data);
}

function AllUsers() {

  const [user, setUsers] = useState();
  useEffect(() => {
    fetchHandler().then((data) => setUsers(data.user));
  }, []);

  // const [searchQuery, setSearchQuery] = useState("");
  // const [noResult, setNoResults] = useState(false);

  // const handleSearch = () => {
  //   fetchHandler().then((data) => {
  //     const filteredUsers = data.users.filter((user) =>
  //       Object.values(user).some((field) =>
  //         field
  //           .toString()
  //           .toLowerCase()
  //           .includes(searchQuery.toLocaleLowerCase())
  //       )
  //     );
  //     setUsers(filteredUsers);
  //     setNoResults(filteredUsers.length === 0);
  //   });
  // };

  return (
    <div>
      <div className="absolute bottom-32 right-56 w-2/4 h-2/4 bg-white p-2 rounded-xl drop-shadow-xl">
        <div class="absolute top-16 left-32 flex items-center border-b w-2/3 border-b-2 border-red-400 py-2">
          <input
            // onChange={(e) => setSearchQuery(e.target.value)}
            class="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none"
            type="text"
            placeholder="Search Users...."
          />
          <Link to="/userDetails">
            <button
              // onClick={handleSearch}
              class="flex-shrink-0 bg-red-400 hover:bg-red-500 border-red-400 hover:border-red-500 text-sm border-4 text-white py-1 px-2 rounded"
              type="button"
            >
              Search
            </button>
          </Link>
        </div>
        <div>
          <Link to="/newUser">
            <button className="absolute top-52 left-32 w-2/3 h-12 bg-red-400 text-white">
              Add new User
            </button>
          </Link>
        </div>
      </div>

        <div>
          {user &&
            user.map((user, i) => (
              <div key={i}>
                <UserDetails user={user} />
              </div>
            ))}
        </div>

      <TopBar />
      <NavBar />
    </div>
  );
}

export default AllUsers;
