import NavBar from "../components/NavBar";
import TopBar from "../components/TopBar";
import { Link } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import Spining from "../components/Spining.jsx";
import { useReactToPrint } from "react-to-print";
import { useRef } from "react";

const URL = "http://localhost:5000/user";

const fetchHandler = async () => {
  try {
    const response = await axios.get(URL);
    return response.data;
  } catch (error) {
    throw new Error(`Error fetching data: ${error.message}`);
  }
};

function AllUsers() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [noResults, setNoResults] = useState(false);

  useEffect(() => {
    fetchHandler()
      .then((data) => {
        console.log(data); // Log the data received from the backend
        setUsers(data.users);
      })
      .catch((error) => {
        console.error('Error fetching data:', error); // Log any errors that occur during fetch
        setError(error); // Update the error state for error handling
      });
  }, []);

  const ComponentsRef = useRef();

  const handleprint = useReactToPrint({
    content: () => ComponentsRef.current,
    DocumentTitle: "user report",
    onafterprint: () => alert("User Report Successfully Download!"),
  });

  const handleSearch = () => {
    fetchHandler().then((data) => {
      const filteredUsers = data.users.filter((user) =>
        user.toString().toLowerCase().includes(searchQuery.toLowerCase())
      );
      setUsers(filteredUsers);
      setNoResults(filteredUsers.length === 0);
    });
  };

  return (
    <div>
      <TopBar />

      <div className="absolute top-8 right-60 w-3/5 h-24 bg-white p-2">
        <div className="absolute top-4 left-12 flex items-center border-b w-1/2 border-b-2 border-red-400 py-2">
          <input
            onChange={(e) => setSearchQuery(e.target.value)}
            className="appearance-none bg-transparent border-none w-full text-gray-700 mr-3 py-1 px-2 leading-tight focus:outline-none"
            type="text"
            placeholder="Search Users...."
          />
          <Link to="/userDetails">
            <button
              onClick={handleSearch}
              className="flex-shrink-0 bg-red-400 hover:bg-red-500 border-red-400 hover:border-red-500 text-sm border-4 text-white py-1 px-2 rounded "
              type="button"
            >
              Search
            </button>
          </Link>
        </div>
        <div>
          <Link to="/newUser">
            <button className="absolute top-4 right-12 w-1/3 h-12 bg-red-400 text-white border-2 border-red-400 rounded-lg translate-y-1">
              Add new User
            </button>
          </Link>
        </div>
      </div>

      {error ? (
        <div>
          <p>Error: {error.message}</p>
        </div>
      ) : noResults ? (
        <div>
          <p>No user found</p>
        </div>
      ) : (
        <div ref={ComponentsRef}>
          {loading ? (
            <Spining />
          ) : (
            <div>
              {users.map((user, index) => (
                <div key={user._id} className="absolute top-48 right-8 w-3/4 h-12">
                  <div className="absolute top-0 right-2 w-full h-auto bg-white p-2 rounded-xl drop-shadow-lg">
                    <div className="pb-4">
                      <h1 className="text-xl font-serif font-semibold">Name</h1>
                      <p className="text-xl font-serif">{user.name}</p>
                    </div>

                    <div className="pb-4">
                      <h1 className="text-xl font-serif font-semibold">User ID</h1>
                      <p className="text-xl font-serif">{user.uID}</p>
                    </div>

                    <div className="pb-4">
                      <h1 className="text-xl font-serif font-semibold">Roll ID</h1>
                      <p className="text-xl font-serif">{user.rID}</p>
                    </div>

                    <div className="pb-4">
                      <h1 className="text-xl font-serif font-semibold">Department ID</h1>
                      <p className="text-xl font-serif">{user.dID}</p>
                    </div>

                    <div className="pb-4">
                      <h1 className="text-xl font-serif font-semibold">Email</h1>
                      <p className="text-xl font-serif">{user.email}</p>
                    </div>
                  </div>

                  <div>
                    <button
                      className="absolute top-96 left-40 bg-red-400 w-40 hover:bg-red-500 drop-shadow-lg text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      type="button"
                      onClick={handleprint}
                    >
                      Detail Report
                    </button>

                    <Link to={`/deleteUser/${user._id}`}>
                      <button
                        className="absolute top-96 right-40 bg-red-400 w-40 hover:bg-red-500 drop-shadow-lg text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        type="button"
                      >
                        Delete
                      </button>
                    </Link>

                    <Link to={`/updateUser/${user._id}`}>
                      <button
                        className="absolute top-96 right-64 -translate-x-60 bg-red-400 w-40 hover:bg-red-500 drop-shadow-lg text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                        type="button"
                      >
                        Update
                      </button>
                    </Link>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      <NavBar />
    </div>
  );
}

export default AllUsers;
